catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
	   g_form.setValue('allow_signup','true');
	   g_form.hideAllFieldMsgs();
      return;
   }
   var event = g_form.getReference('club_event',checkCapacity);
   function checkCapacity(event){
	var ga = new GlideAjax('x_snc_commodore_0.CCUtils');
	ga.addParam('sysparm_name','getBevAjax');
	ga.addParam('sysparm_event_id',event.sys_id);
	ga.getXML(getAnswer);

	function getAnswer(response){
		var answer = response.responseXML.documentElement.getAttribute('answer');
		var count = answer == '' ? 0 : answer.split(',').length;
		if(count>=event.slots){
			g_form.setValue('allow_signup','false');
			g_form.showFieldMsg("event_details","Sorry, this event's beverage list is full. Please contact the Commodore's Club Founders if you want to register a beverage.","error",true);

		} else {
			g_form.setValue('allow_signup','true');
			g_form.hideFieldMsg("event_details",true);
		}
	}   
   } 

   var event2 = g_form.getReference('club_event',checkSignup);

	function checkSignup(event2){
		//console.log(event.sys_id);
		var ga = new GlideAjax('x_snc_commodore_0.CCUtils');
		ga.addParam('sysparm_name','getSignupAjax');
		ga.addParam('sysparm_event_id',event2.sys_id);
		ga.addParam('sysparm_user_id',g_form.getValue('user_details'));
		ga.getXML(getAnswer2);

		function getAnswer2(response){
			var answer = response.responseXML.documentElement.getAttribute('answer');
			//console.log(answer);
			if(answer=='false'){
				g_form.setValue('allow_signup','false');
				g_form.showFieldMsg("event_details","You have not registered for this event. Please register before signing up for a beverage.","error",true);

			}
		}
	} 
}
