sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.options = options;
	if(data.options.beverage){
		//console.log(data.options.beverage);
		data.bev_id = data.options.beverage;
		buildBev(data.bev_id);
	}

	function buildBev(bev_id){
		data.bev = {};
		if(bev_id != ''){
			var bev = new GlideRecord('x_snc_commodore_0_beverage');
			bev.get(bev_id);
			var fields = $sp.getFields(bev,'name,masked_name,brand,style,description,country,region,abv,proof,type,type_other,taste,sys_created_on,introduced_by,event');
			for(i in fields){
				var field = fields[i];
				//console.log(field);
				if(field.name && field.display_value && field.display_value !=''){
					data.bev[field.name] = field;
				}
			}
			if(!data.bev.type && data.bev.type_other){
				data.bev.type = data.bev.type_other;
			}
			console.log(data.bev.event.value);
			var name_hidden = new x_snc_commodore_0.CCUtils().getBevVisible(data.bev.event.value);
			console.log(name_hidden);
			if(name_hidden){
				data.bev.name = data.bev.masked_name;
				data.bev.brand = null;
			}

			//console.log(data.bev);
		}
	}
})();
