sp_widget.client_script:
api.controller=function($scope,$rootScope,$timeout,spUtil) {
  /* widget controller */
  var c = this;
  var data = c.data;

	$rootScope.$on('cat_item_id', function(event,parms) {
		console.log('receiving cat_item_id');
			$scope.data.action = 'cat_item';
			$scope.data.event_id = parms;
			$scope.data.input = {
				type: 'cat_item',
				event_id: parms
		};
		//$timeout(function(){					
			//c.server.update().then(function(e){
			spUtil.update($scope).then(function(e){
				console.log('Hello from the promise!');	
			});
		//},200);
		//console.log(parms);
	});

	c.checkAttendance = function(id){
		var a_list = c.data.a_list;

		var chkEvent = new GlideRecord('x_snc_commodore_0_events');
		chkEvent.get(id);
		console.log(a_list);
		console.log(chkEvent.slots);
		if(a_list.length >= chkEvent.slots){
			$scope.page.g_form.setValue('allow_signup','false');
			console.log('no more attendees');
		}

	};

};

