sp_widget.client_script:
api.controller=function(spModal) {
  /* widget controller */
  var c = this;
  var data = c.data;

  c.clickCard = function(bev,options){
		options.display = 'full';
		//console.log(bev);
		//console.log(options);
		var buttons = [{
					primary: true,
					label: 'Ok'
			}];
		var vote = false;
		if(options.click_action == 'vote' && !options.buttons){	
			//console.log(options.beverage);
			vote = true;
			buttons = [
				{	cancel: true,
					label: 'Cancel'
				},{
					primary: true,
					label: 'Vote!'
			}];
		}

		spModal.open({
			title: data.modal_title || 'More Information',
			widget: 'cc-beverage-card',
			widgetInput: options,
			buttons: buttons
		}).then(function(){
			//console.log("Vote will be generated for "+options.attendanceRecord);
			if(vote){
				//$scope.data.voted = options.beverage;
				$rootScope.$emit('clicked_beverage',options.beverage);
			}
			console.log('Modal closed');
		});

  };
};

sp_widget.css:
.cc-beverage-list {
	white-space: nowrap;
}

.cc-list-row.cards {
	overflow-x: auto;
    padding: 2rem;
    scroll-snap-type: x mandatory;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var user = gs.getUserID();
	if(!input){
		data.options = options;
	} else{
		data.options = input;
	}
	console.log('Bev List Options');
	console.log(data.options);
	data.limit = data.options.limit;
	data.bevs = [];
	data.widgets = [];
	data.event_page = false;

	var constants = new x_snc_commodore_0.CCUtils().getConstants();

	if($sp.getParameter('id') == 'event'){
		data.event_page = true;
		data.modal_title = 'Cellar Treasure Details';
	}
	if($sp.getParameter('id') == 'live_event'){
		data.event_page = true;
		data.live_page = true;
		data.modal_title = 'Cast Your Vote!';
	}

	var bevs = new GlideRecord('x_snc_commodore_0_beverage');
	bevs.addQuery('state','approved');	
	if(data.options.event){
		bevs.addQuery('event',data.options.event);
		console.log('OPTIONS EVENT: '+data.options.event);
		data.event = data.options.event;
	} else if(!data.options.event && data.event_page && $sp.getParameter('sys_id')){
		bevs.addQuery('event',$sp.getParameter('sys_id'));
		data.event = $sp.getParameter('sys_id');
		console.log('PARAM EVENT: '+data.event);
	}
	data.bev_href = "/cc?id=sc_cat_item&sys_id="+constants.CAT_ITEM_BEVSIGNUP+"&event_id="+data.event;

	if(data.event_page){
		var bc = new x_snc_commodore_0.CCUtils().getBevCapacity(data.event);
		data.bev_capacity = bc;
	}

	if(data.options.click_action == 'vote'){
		var attendanceRecord = new x_snc_commodore_0.CCUtils().getMyRSVPRecord(user,data.event).sys_id.toString();
		var voteRecord = new x_snc_commodore_0.CCUtils().getMyVote(user,attendanceRecord);
		//console.log(voteRecord);
	}

	bevs.orderBy('masked_name');
	bevs.query();

	while(bevs.next()){		
		var bev = {};
		bev.sys_id = bevs.getValue('sys_id');
		var voted = false;
		if(bev.sys_id == data.options.voted){ voted = true; }
		bev.event = bevs.getValue('event');
		bev.options = {beverage:bev.sys_id,event_id:bev.event,display:'list',click_action:data.options.click_action};
		if(data.options.click_action == 'vote'){
			bev.options.user = user;
			bev.options.attendanceRecord = attendanceRecord;
			bev.options.voted = voted;
		}
		//console.log(['bev',bev]);
		data.widgets.push($sp.getWidget("cc-beverage-card",bev.options));
		data.bevs.push(bev);
		//console.log('BEV:');
		//console.log(bev);
	}
	// console.log('WIDGETS:');
	// console.log(data.widgets);
	// console.log('BEVS:');
	// console.log(data.bevs);
})();
