sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  var constants = new x_snc_commodore_0.CCUtils().getConstants();


  var user = gs.getUserID();

  //data.rsvp.state = '';

  if ($sp.getParameter('id')=='event'&& $sp.getParameter('sys_id')!=''){
		//console.log('no input received');
		data.event = $sp.getParameter('sys_id');	
		data.signup_href = "/cc?id=sc_cat_item&sys_id="+constants.CAT_ITEM_EVENTSIGNUP+"&event_id="+data.event;

		var ea = new GlideRecord('x_snc_commodore_0_club_event_attendances');
		ea.addQuery('event',data.event);
		ea.addQuery('user',user);
		ea.query();
		if(ea.next()){
			console.log('Found event attendance for data: '+[data.event,user,ea.getValue('sys_id')]);
			data.rsvp = {
				state: {value: ea.getDisplayValue('state'), display_value: ea.getValue('state')},
				created: ea.getValue('sys_created_on')
			};

			//Date handling
			var v_gdt = data.rsvp.created;
			var gdt = new GlideDateTime(v_gdt);
			//user getLocalDate/Time for SN TZ 
			var date = gdt.getLocalDate().getByFormat('MMMM d, YYYY');
			var time = gdt.getLocalTime().getByFormat('hh:mm a');
			data.rsvp.date = date;
			data.rsvp.time = time;
			console.log(data.rsvp);
		} else {

		}


	}

})();
