sp_widget.client_script:
api.controller=function(spUtil) {
  /* widget controller */
  var c = this;
 var data = c.data;
		spUtil.get("cc-event-card",{action:'cat_item',event_id:data.event,'display':'feature'}).then(function(response){
			c.eventWidget = response;
		});
};

sp_widget.css:
.cc-cellar-card {
	display: grid; 
  grid-template-columns: 2fr 1fr; 
  grid-template-rows: 1fr; 
  grid-auto-rows: 1fr
  gap: 5px 5px; 
}

.left-area {
	display: flex;
  flex-direction: column;
  
  .cellar-img {
  	align-self: center;
  }
  
