sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.user = gs.getUserID();
	data.event = '';
	var live = new GlideRecord('x_snc_commodore_0_events');
	live.addQuery('voting_open',true);
	live.query();
	if(live.next()){
		var registered = new GlideRecord('x_snc_commodore_0_club_event_attendances');
		registered.addQuery('event',live.sys_id);
		registered.addQuery('user',data.user);
		registered.query();
		if(registered.next()){
			data.event = live.sys_id;
			data.live_href = "/cc?id=live_event&sys_id="+data.event;
		}
	}

})();
